	public static int[][] updateMatrixBSF(int[][] matrix) {
		if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
			return null;
		}
		int[][] result = new int[matrix.length][matrix[0].length];
		int[][] queue = new int[matrix.length * matrix[0].length][];
		int qIndex = -1, qSize = -1;

		for (int i = 0; i < matrix.length; i++) {
			for (int j = 0; j < matrix[0].length; j++) {
				if (matrix[i][j] == 0) {
					queue[++qSize] = new int[] {i, j};
				} else {
					result[i][j] = Integer.MAX_VALUE;
				}
			}
		}

		while (qIndex < qSize) {
			int[] c = queue[++qIndex];
			if (bsf(result, c[0] - 1, c[1], queue, qSize, result[c[0]][c[1]])) qSize++;
			if (bsf(result, c[0] + 1, c[1], queue, qSize, result[c[0]][c[1]])) qSize++;
			if (bsf(result, c[0], c[1] - 1, queue, qSize, result[c[0]][c[1]])) qSize++;
			if (bsf(result, c[0], c[1] + 1, queue, qSize, result[c[0]][c[1]])) qSize++;
		}

		return result;
	}

	private static boolean bsf(int[][] result, int i, int j, int[][] queue, int qSize, int distance) {
		if (i < 0 || j < 0 || i >= result.length || j >= result[0].length || result[i][j] <=  distance + 1) {
			return false;
		}
		result[i][j] = distance + 1;
		queue[++qSize] = new int[] {i, j};
		return true;
	}

	public static int[][] updateMatrixDSF(int[][] matrix) {
		if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
			return null;
		}
		int[][] result = new int[matrix.length][matrix[0].length];

		for (int i = 0; i < matrix.length; i++) {
			for (int j = 0; j < matrix[0].length; j++) {
				result[i][j] = Integer.MAX_VALUE;
			}
		}

		for (int i = 0; i < matrix.length; i++) {
			for (int j = 0; j < matrix[0].length; j++) {
				if (matrix[i][j] == 0) {
					result[i][j] = 0;
					dfs(matrix, result, i - 1, j, 1);
					dfs(matrix, result, i + 1, j, 1);
					dfs(matrix, result, i, j - 1, 1);
					dfs(matrix, result, i, j + 1, 1);
				}
			}
		}
		return result;
	}

	private static void dfs(int[][] matrix, int[][] result, int i, int j, int distance) {
		if (i < 0 || j < 0 || i >= matrix.length || j >= matrix[0].length || matrix[i][j] == 0 || result[i][j] <= distance) {
			return;
		}

		result[i][j] = distance;
		dfs(matrix, result, i - 1, j, distance + 1);
		dfs(matrix, result, i + 1, j, distance + 1);
		dfs(matrix, result, i, j - 1, distance + 1);
		dfs(matrix, result, i, j + 1, distance + 1);
	}
